function initMap(){map=new google.maps.Map(document.getElementById("map-canvas"),{center:{lat:34.751546,lng:-111.766356},zoom:9,disableDefaultUI:!0});var a=new google.maps.LatLng(34.8697289,-111.7610634);new google.maps.Marker({position:a,map:map})}var form_title_btn=document.querySelector(".form-title-btn"),search_form=document.querySelector(".search-form"),calendar=search_form.querySelectorAll(".icon-calendar");form_title_btn.addEventListener("click",function(a){a.preventDefault(),search_form.classList.contains("modal-content-show")?(search_form.classList.remove("modal-content-show"),search_form.classList.add("modal-content-hide")):(search_form.classList.add("modal-content-show"),search_form.classList.remove("modal-content-hide"))});for(var i=0;i<calendar.length;i++)calendar[i].addEventListener("click",function(a){a.preventDefault(),alert("Заглушка")});var map;search_form.addEventListener("click",function(a){var b=a.target;if("BUTTON"==b.tagName){a.preventDefault();var c=b.parentNode.children[1],d=parseInt(c.value,10);"icon-minus"==b.className?d--:d++,d<0&&(d=0),c.value=d}});